            while(newShipSpots.includes(randomNumber)){
                randomNumber = Math.floor(Math.random() * 99)
            }
            newShipSpots.push(randomNumber);
        }
        this.setState({shipSpots: newShipSpots, counter: 25})
        console.log(newShipSpots)
    }
    handleLocation = (index) => {
        let {board, counter} = this.state
        if(board[index] === "") {
            counter--
            board[index] = "ðŸ’¥"
        }
        else if(shipSpots.includes(index)){
            counter--
            board[index] = "H"
        }
            this.setState({board: board, counter: counter})
        }
        render(){
              let square = this.state.board.map((value, index) => {
                return (
                    <Square
                        handleLocation={ this.handleLocation }
                        index={ index }
                        value={ value }
                        key={ index }
                    />

                )
              }
          )


              return(
              <>
              <div className="board">
                  { square }
                  Counter: {this.state.counter}
                  <button onClick={this.startGame}> Start Game </button>
              </div>
              </>
              );
          }
      }


export default Board
